// Impor komponen Kegiatan.vue
import Kegiatan from './components/Kegiatan.vue';
import axios from 'axios'; // Import axios untuk melakukan HTTP requests

export default {
  name: 'App',
  components: {
    // Tambahkan komponen Kegiatan ke dalam daftar komponen
    Kegiatan
  },
  data() {
    return {
      users: [],
      posts: []
    };
  },
  watch: {
    // Mengamati perubahan pada data users
    users: {
      handler(newVal) {
        console.log('Data user telah berubah:', newVal);
        // Lakukan sesuatu ketika data user berubah
      },
      deep: true // Watcher akan berjalan secara rekursif untuk objek dalam array
    },
    // Mengamati perubahan pada data posts
    posts: {
      handler(newVal) {
        console.log('Data postingan telah berubah:', newVal);
        // Lakukan sesuatu ketika data postingan berubah
      },
      deep: true // Watcher akan berjalan secara rekursif untuk objek dalam array
    }
  },
  created() {
    // Ambil data user dari JSONPlaceholder saat komponen dibuat
    axios.get('https://jsonplaceholder.typicode.com/users').then(response => {
      this.users = response.data;
    }).catch(error => {
      console.error('Terjadi kesalahan saat mengambil data user:', error);
    });

    // Ambil data postingan dari JSONPlaceholder saat komponen dibuat
    axios.get('https://jsonplaceholder.typicode.com/posts').then(response => {
      this.posts = response.data;
    }).catch(error => {
      console.error('Terjadi kesalahan saat mengambil data postingan:', error);
    });
  }
};
